mapper.select:
SELECT
    CaseId,PetitionUserId
FROM
    case_relations
WHERE (PetitionUserInfo_Email =  #{email}
    OR Agent1_Email =  #{email}
    OR Agent2_Email =  #{email}
    OR Agent3_Email =  #{email}
    OR Agent4_Email =  #{email}
    OR Agent5_Email = #{email})
    AND DeleteFlag =  0
ORDER BY CaseId ASC

SELECT 
    CaseId,PetitionUserId
FROM
    case_relations
WHERE (TraderUserEmail = #{email}
    OR TraderAgent1_UserEmail = #{email}
    OR TraderAgent2_UserEmail = #{email}
    OR TraderAgent3_UserEmail = #{email}
    OR TraderAgent4_UserEmail = #{email}
    OR TraderAgent5_UserEmail = #{email})
    AND DeleteFlag = 0
ORDER BY CaseId ASC
