mapper.update:
UPDATE
    cases
SET
    NegotiationEndDateChangeStatus = #{NegotiationEndDateChangeStatus},
    NegotiationEndDateChangeCount = #{NegotiationEndDateChangeCount},
    LastModifiedDate = NOW(),
    LastModifiedBy = #{UserId}
WHERE
    cid = #{CaseId}
    AND PlatformId = #{PlatformId}
